package antBrain;

import engine.DummyEngine;

/**
 * Used by the DummyEngine to get Brain objects from file, or generated by the GA
 * 
 * @author pkew20 / 57116
 * @version 1.0
 */
public class BrainController {
	public static Brain readBrainFrom(String path) {
		return BrainParser.readBrainFrom(path);
	}
	
	public static Brain getBestGABrain(DummyEngine dummyEngine, int epochs, int popSize, int mutationRate) {
		GA ga = new GA();
		ga.createPopulation(dummyEngine, popSize);
		ga.evolve(dummyEngine, epochs, mutationRate);
		return ga.getBestBrain();
	}
}
